from knnmodel import run_knn_modelfrom dtreemodel import run_dtree_modelfrom svmmodel import run_svm_modeldef main():    print("Select a model to run:")    print("1. K-Nearest Neighbors (KNN)")    print("2. Decision Tree")    print("3. Support Vector Machine (SVM)")    choice = input("Enter the number of the model to run: ")    if choice == "1":        run_knn_model()    elif choice == "2":        run_dtree_model()    elif choice == "3":        run_svm_model()    else:        print("Invalid choice.")if __name__ == "__main__":    main()